@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
	:root {
		--background: 0 0% 100%;
		--foreground: 240 10% 3.9%;
		--card: 0 0% 100%;
		--card-foreground: 240 10% 3.9%;
		--popover: 0 0% 100%;
		--popover-foreground: 240 10% 3.9%;
		--primary: 240 5.9% 10%;
		--primary-foreground: 0 0% 98%;
		--secondary: 240 4.8% 95.9%;
		--secondary-foreground: 240 5.9% 10%;
		--muted: 240 4.8% 95.9%;
		--muted-foreground: 240 3.8% 46.1%;
		--accent: 240 4.8% 95.9%;
		--accent-foreground: 240 5.9% 10%;
		--destructive: 0 84.2% 60.2%;
		--destructive-foreground: 0 0% 98%;
		--border: 240 5.9% 90%;
		--input: 240 5.9% 90%;
		--ring: 240 10% 3.9%;
		--chart-1: 12 76% 61%;
		--chart-2: 173 58% 39%;
		--chart-3: 197 37% 24%;
		--chart-4: 43 74% 66%;
		--chart-5: 27 87% 67%;
		--radius: 0.5rem;
		--sidebar-background: 0 0% 98%;
		--sidebar-foreground: 240 5.3% 26.1%;
		--sidebar-primary: 240 5.9% 10%;
		--sidebar-primary-foreground: 0 0% 98%;
		--sidebar-accent: 240 4.8% 95.9%;
		--sidebar-accent-foreground: 240 5.9% 10%;
		--sidebar-border: 220 13% 91%;
		--sidebar-ring: 217.2 91.2% 59.8%;
	}
	.dark {
		--background: 240 10% 3.9%;
		--foreground: 0 0% 98%;
		--card: 240 10% 3.9%;
		--card-foreground: 0 0% 98%;
		--popover: 240 10% 3.9%;
		--popover-foreground: 0 0% 98%;
		--primary: 0 0% 98%;
		--primary-foreground: 240 5.9% 10%;
		--secondary: 240 3.7% 15.9%;
		--secondary-foreground: 0 0% 98%;
		--muted: 240 3.7% 15.9%;
		--muted-foreground: 240 5% 64.9%;
		--accent: 240 3.7% 15.9%;
		--accent-foreground: 0 0% 98%;
		--destructive: 0 62.8% 30.6%;
		--destructive-foreground: 0 0% 98%;
		--border: 240 3.7% 15.9%;
		--input: 240 3.7% 15.9%;
		--ring: 240 4.9% 83.9%;
		--chart-1: 220 70% 50%;
		--chart-2: 160 60% 45%;
		--chart-3: 30 80% 55%;
		--chart-4: 280 65% 60%;
		--chart-5: 340 75% 55%;
		--sidebar-background: 240 5.9% 10%;
		--sidebar-foreground: 240 4.8% 95.9%;
		--sidebar-primary: 224.3 76.3% 48%;
		--sidebar-primary-foreground: 0 0% 100%;
		--sidebar-accent: 240 3.7% 15.9%;
		--sidebar-accent-foreground: 240 4.8% 95.9%;
		--sidebar-border: 240 3.7% 15.9%;
		--sidebar-ring: 217.2 91.2% 59.8%;
	}
	:root {
		/* Gruvbox Light Palette */
		--background: 44 56% 94%; /* #fbf1c7 */
		--foreground: 30 7% 23%; /* #3c3836 */
		--card: 44 56% 94%; /* #fbf1c7 */
		--card-foreground: 30 7% 23%; /* #3c3836 */
		--popover: 44 56% 94%; /* #fbf1c7 */
		--popover-foreground: 30 7% 23%; /* #3c3836 */
		--primary: 2 92% 32%; /* #9d0006 (faded_red) */
		--primary-foreground: 44 56% 94%; /* #fbf1c7 */
		--secondary: 64 78% 25%; /* #79740e (faded_green) */
		--secondary-foreground: 44 56% 94%; /* #fbf1c7 */
		--muted: 38 18% 57%; /* #a89984 (light4) */
		--muted-foreground: 30 7% 23%; /* #3c3836 */
		--accent: 38 69% 41%; /* #b57614 (faded_yellow) */
		--accent-foreground: 44 56% 94%; /* #fbf1c7 */
		--destructive: 0 73% 46%; /* #cc241d (neutral_red) */
		--destructive-foreground: 44 56% 94%; /* #fbf1c7 */
		--border: 39 35% 78%; /* #d5c4a1 (light2) */
		--input: 39 35% 78%; /* #d5c4a1 (light2) */
		--ring: 38 69% 41%; /* #b57614 (faded_yellow) */
		--chart-1: 29 96% 59%; /* #fe8019 (bright_orange) */
		--chart-2: 82 60% 42%; /* #b8bb26 (bright_green) */
		--chart-3: 45 96% 57%; /* #fabd2f (bright_yellow) */
		--chart-4: 186 23% 56%; /* #83a598 (bright_blue) */
		--chart-5: 329 44% 52%; /* #d3869b (bright_purple) */
		--radius: 0.5rem;
		--sidebar-background: 48 62% 97%; /* #f9f5d7 (light0_hard) */
		--sidebar-foreground: 30 7% 23%; /* #3c3836 */
		--sidebar-primary: 2 92% 32%; /* #9d0006 */
		--sidebar-primary-foreground: 44 56% 94%; /* #fbf1c7 */
		--sidebar-accent: 38 69% 41%; /* #b57614 */
		--sidebar-accent-foreground: 44 56% 94%; /* #fbf1c7 */
		--sidebar-border: 39 35% 78%; /* #d5c4a1 */
		--sidebar-ring: 38 69% 41%; /* #b57614 */
	}
	.dark {
		/* Gruvbox Dark Palette */
		--background: 30 7% 16%; /* #282828 */
		--foreground: 44 56% 86%; /* #ebdbb2 */
		--card: 30 7% 16%; /* #282828 */
		--card-foreground: 44 56% 86%; /* #ebdbb2 */
		--popover: 30 7% 16%; /* #282828 */
		--popover-foreground: 44 56% 86%; /* #ebdbb2 */
		--primary: 2 96% 60%; /* #fb4934 (bright_red) */
		--primary-foreground: 30 7% 16%; /* #282828 */
		--secondary: 82 60% 42%; /* #b8bb26 (bright_green) */
		--secondary-foreground: 30 7% 16%; /* #282828 */
		--muted: 38 11% 54%; /* #928374 (gray_245) */
		--muted-foreground: 44 56% 86%; /* #ebdbb2 */
		--accent: 45 96% 57%; /* #fabd2f (bright_yellow) */
		--accent-foreground: 30 7% 16%; /* #282828 */
		--destructive: 0 73% 46%; /* #cc241d (neutral_red) */
		--destructive-foreground: 44 56% 86%; /* #ebdbb2 */
		--border: 24 9% 23%; /* #3c3836 (dark1) */
		--input: 24 9% 23%; /* #3c3836 (dark1) */
		--ring: 44 64% 48%; /* #d79921 (neutral_yellow) */
		--chart-1: 29 96% 59%; /* #fe8019 (bright_orange) */
		--chart-2: 82 60% 42%; /* #b8bb26 (bright_green) */
		--chart-3: 45 96% 57%; /* #fabd2f (bright_yellow) */
		--chart-4: 186 23% 56%; /* #83a598 (bright_blue) */
		--chart-5: 329 44% 52%; /* #d3869b (bright_purple) */
		--sidebar-background: 20 11% 12%; /* #1d2021 (dark0_hard) */
		--sidebar-foreground: 44 56% 86%; /* #ebdbb2 */
		--sidebar-primary: 2 96% 60%; /* #fb4934 */
		--sidebar-primary-foreground: 30 7% 16%; /* #282828 */
		--sidebar-accent: 45 96% 57%; /* #fabd2f */
		--sidebar-accent-foreground: 44 56% 86%; /* #ebdbb2 */
		--sidebar-border: 24 9% 23%; /* #3c3836 */
		--sidebar-ring: 44 64% 48%; /* #d79921 */
	}
}

@layer base {
	* {
		@apply border-border;
	}
	html,
	body {
		transition:
			background-color 0.3s,
			color 0.3s,
			border-color 0.3s,
			fill 0.3s,
			stroke 0.3s;
	}
	body {
		@apply bg-background text-foreground;
	}
}

[data-rehype-pretty-code-fragment] {
	@apply relative text-white;
}

[data-rehype-pretty-code-fragment] code {
	@apply grid min-w-full break-words rounded-none border-0 bg-transparent p-0;
	counter-reset: line;
	box-decoration-break: clone;
}

[data-rehype-pretty-code-fragment] .line {
	@apply px-4 min-h-[1rem] py-0.5 w-full inline-block;
}

[data-rehype-pretty-code-fragment] [data-line-numbers] .line {
	@apply px-2;
}

[data-rehype-pretty-code-fragment] [data-line-numbers] > .line::before {
	@apply text-zinc-50/40 text-xs;
	counter-increment: line;
	content: counter(line);
	display: inline-block;
	width: 1.8rem;
	margin-right: 1.4rem;
	text-align: right;
}

[data-rehype-pretty-code-fragment] .line--highlighted {
	@apply bg-zinc-700/50;
}

[data-rehype-pretty-code-fragment] .line-highlighted span {
	@apply relative;
}

[data-rehype-pretty-code-fragment] .word--highlighted {
	@apply rounded-md bg-zinc-700/50 border-zinc-700/70 p-1;
}

.dark [data-rehype-pretty-code-fragment] .word--highlighted {
	@apply bg-zinc-900;
}

[data-rehype-pretty-code-title] {
	@apply mt-2 pt-6 px-4 text-sm font-medium text-foreground;
}

[data-rehype-pretty-code-title] + pre {
	@apply mt-2;
}
